function t300_init(){var tooltipBlocks=document.querySelectorAll('.t300');Array.prototype.forEach.call(tooltipBlocks,function(tooltip){var hook=tooltip.getAttribute('data-tooltip-hook');var tooltipId=tooltip.getAttribute('data-tooltip-id');if(hook!==''){if(typeof window.tooltipster!=='undefined'){var links=document.querySelectorAll('a[href="'+hook+'"]:not(.tooltipstered)')}else{if(typeof jQuery!=='undefined'){var links=$('a[href="'+hook+'"]:not(.tooltipstered)')}}
if(!links||links.length===0)return;var content=tooltip.querySelector('.t300__content').innerHTML;var touchDevices=!1;if(hook.charAt(0)=='#'){touchDevices=!0}
var position=tooltip.getAttribute('data-tooltip-position');if(position===''){position='top'}
t_onFuncLoad('tooltipster',function(){window.tooltipster(links,{theme:'t300__tooltipster-noir t300__tooltipster-noir_'+tooltipId+'',contentAsHTML:!0,content:content,interactive:!0,touchDevices:touchDevices,position:position})});for(var i=0;i<links.length;i++){var link=links[i];link.addEventListener('tildatooltip:show',function(){var popup=link.closest('.t-popup');if(!popup)return;var style=document.createElement('style');style.id='t300TooltipStyle';style.innerHTML='.tooltipster-base {z-index: 9999999;}';document.head.append(style)});link.addEventListener('tildatooltip:hide',function(){var style=document.getElementById('t300TooltipStyle');if(style)style.remove()})}}})}
t_onReady(function(){t300_init();if('MutationObserver' in window){setTimeout(function(){var stores=document.querySelectorAll('body .t-store');Array.prototype.forEach.call(stores,function(store){new MutationObserver(function(mutationsList){for(var mutation in mutationsList){var event=mutationsList[mutation];if(event.type==='attributes'){if(event.target.className.indexOf('t-popup_show')!=-1){t300_init()}}}}).observe(store,{attributes:!0,attributeFilter:['class'],subtree:!0})});t300_init()},500)}})